import { useState } from "react";
import nextValue from "../../helpers/nextValue";
import ButtonOption from "../ui/buttons/ButtonOption";

const OptionSize = (props) => {
  const sizes = Object.keys(props.data.variant);
  const initialState = sizes[0]
  const [optionSize, setOptionSize] = useState(initialState);

  const sizeHandler = () => {
    setOptionSize(nextValue(sizes, optionSize))
  };

  return (
    <ButtonOption onClickHandler={sizeHandler}>
      {`${props.data.variant[optionSize].dimensions[0]}" x ${props.data.variant[optionSize].dimensions[0]}"`}
    </ButtonOption>
  );
};

export default OptionSize;
