import { useSelector } from "react-redux";
import OptionSize from "../elements/OptionSize";
import OptionQuantity from "../elements/OptionQuantity";

const CartContainer = () => {
  const cart = useSelector((state) => state.cart);
  const changeQuantity = (value) => {
    cart[cart.indexOf(element)].quantity = value
  }
  const cartGallery = cart.map((element) => {
    const dimensions = `${element.variant.dimensions[0]} x ${element.variant.dimensions[1]}`

    return (
      <div key={`${element.id}.${element.variant.id}`}>
        <h2>{element.name}</h2>
        <p>size: {dimensions}</p>
        <OptionQuantity value={element.quantity} setValue={changeQuantity}/>
        <p>price: {element.price}</p>
        <button>remove</button>
      </div>
    );
  });

  return (
    <>
      <p>
        {cart.length > 0
          ? `${cart.reduce(
              (prevVal, nextVal) => prevVal + nextVal.quantity,
              0
            )} items in your shopping bag`
          : "there is no items in your shopping bag"}
      </p>
      <div>{cartGallery}</div>
    </>
  );
};

export default CartContainer;
