const inCart = (cart, product) => {
  for (let el of cart) {
    console.log(`iterable element id: ${el.id}.${el.variant.id}`);
    console.log(`chosen product id: ${product.id}.${product.variant.id}`);
    if (`${el.id}.${el.variant.id}` === `${product.id}.${product.variant.id}`) {
      console.ol(cart.indexOf(el));
    } else return false;
  }
};

export default inCart;
