import { useSelector } from "react-redux";

const CartContainer = () => {
  const cart = useSelector((state) => state.cart);

  return (
    <p>
      {cart.lenght > 0
        ? `${cart.reduce((prevValue, nextValue) => {
            return prevValue + nextValue.quantity;
          })} items in your shopping bag`
        : "there is no items in your shopping bag"}
    </p>
  );
};

export default CartContainer;
