const useSortProducts = (type) => {
    switch (type) {
        case 'price':
            console.log('sort by price')
            break;
        case 'popularity':
            console.log('sort by popularity')
            break;
        case 'author':
            console.log('sort by author')
            break;
        case 'name':
            console.log('sort by name')
            break;
        case 'date released':
            console.log('sort by date released')
            break;
    }
}

export default useSortProducts
