import { useSelector } from "react-redux";

const CartContainer = () => {
  const cart = useSelector((state) => state.cart.cart);
  const itemsInCart = cart.reduce((prevValue, nextValue) => {
    return prevValue + nextValue.quantity
  })

  return <p>{cart.lenght > 0 ? `${itemsInCart} items in your shopping bag` : 'there is no items in your shopping bag'}</p>;
};

export default CartContainer;
