const sortProducts = (products, callbackFunction, order) => {
  if (order === "up") {
    products.sort((a, b) =>
      callbackFunction(a) > callbackFunction(b) ? 1 : -1
    );
  } else {
    products.sort((a, b) =>
      callbackFunction(a) < callbackFunction(b) ? 1 : -1
    );
  }
};

export default sortProducts;
