import ButtonOption from "../ui/buttons/ButtonOption";
import { useState } from "react";
import { cartActions } from "../../store/cart-slice";
import { useDispatch } from "react-redux";

const OptionQuantity = (props) => {
  const [quantity, setQuantity] = useState(props.initValue);
  const type = props.type;
  const dispatch = useDispatch();

  const onDecrease = () => {
    const newQuantity = quantity - 1
    if (quantity > 1) {
      setQuantity(newQuantity);

      switch (type) {
        case "cart":
          dispatch(cartActions.decreaseQuantity(props.index));
          break;
        case "product":
          props.getter(newQuantity);
          break;
      }
    }
  };

  const onIncrease = () => {
    const newQuantity = quantity + 1
    setQuantity(newQuantity);

    switch (type) {
      case "cart":
        dispatch(cartActions.increaseQuantity(props.index));
        break;
      case "product":
        props.getter(newQuantity);
        break;
    }
  };

  return (
    <>
      <ButtonOption onClickHandler={onDecrease}>-</ButtonOption>
      <p>{quantity}</p>
      <ButtonOption onClickHandler={onIncrease}>+</ButtonOption>
    </>
  );
};

export default OptionQuantity;
