import { useDispatch, useSelector } from "react-redux";
import GalleryCart from "../ui/galleries/GalleryCart";

const CartContainer = () => {
  const cart = useSelector((state) => state.cart);
  const subtotal = cart.reduce((cur, prev) => {
    cur.price = (cur.price * cur.quantity + prev.price * prev.quantity)/cur.quantity
  })

  return (
    <>
      <p>
        {cart.length > 0
          ? `${cart.reduce(
              (prevVal, nextVal) => prevVal + nextVal.quantity,
              0
            )} items in your shopping bag`
          : "there is no items in your shopping bag"}
      </p>
      <GalleryCart data={cart}/>
      <p>subtotal: {subtotal}</p>
      <button></button>
    </>
  );
};

export default CartContainer;
