import { useSelector } from "react-redux";
import GalleryCart from "../ui/galleries/GalleryCart";

const CartContainer = () => {
  const cart = useSelector((state) => state.cart);
  const empty = cart.lenght > 0 ? true : false;
  const subtotal = cart.reduce((cur, prev) => {
    cur =
      (cur.price * cur.quantity + prev.price * prev.quantity) / cur.quantity;
  }, 0);

  return (
    <div>
      <p>
        {empty
          ? `${cart.reduce(
              (prevVal, nextVal) => prevVal + nextVal.quantity,
              0
            )} items in your shopping bag`
          : "there is no items in your shopping bag"}
      </p>
      <GalleryCart data={cart} />
      {empty && <p>subtotal: {subtotal}</p>}
      <button>check out</button>
      <button>continue shopping</button>
    </div>
  );
};

export default CartContainer;
