import ButtonOption from "../ui/buttons/ButtonOption";
import { useState } from "react";
import nextValue from "../../helpers/nextValue";
import sortProducts from "../../helpers/sortProducts";

const OptionSort = (props) => {
  const optionValues = [
    "price",
    "popularity",
    "author",
    "name",
    "date released",
  ];
  const optionOrder = ["up", "down"];

  const [optionSettings, setOptionSettings] = useState({
    value: optionValues[1],
    order: optionOrder[0],
  });

  const valueHandler = () => {
    const newValue = nextValue(optionValues, optionSettings.value);
    console.log(`new value of option ${newValue}`)
    setOptionSettings({...optionSettings, value: newValue})
    sortProducts(newValue);
  };

  const orderHandler = () => {
    const newOrder = nextValue(optionOrder, optionSettings.order);
    console.log(`new value of option ${newValue}`)
    setOptionSettings({...optionSettings, order: newOrder})
    sortProducts(newOrder);
  };

  const option = (
    <>
      <button onClick={valueHandler} />
      <ButtonOption onClickHandler={orderHandler}>
        {optionSettings.value}
      </ButtonOption>
      <ButtonOption>{optionSettings.order}</ButtonOption>
    </>
  );

  return option;
};

export default OptionSort;
