import { useSelector } from "react-redux";
import { cartActions } from "../../store/cart-slice";
import OptionQuantity from "../elements/OptionQuantity";

const CartContainer = () => {
  const cart = useSelector((state) => state.cart);

  const cartGallery = cart.map((element, index) => {
    const dimensions = `${element.variant.dimensions[0]}" x ${element.variant.dimensions[1]}"`;

    const getQuantity = (val) => val;
    const onRemove = () => {
      dispatch(cartActions.removeFromCart(index))
    }

    return (
      <div key={`${element.id}.${element.variant.id}`}>
        <h2>{element.name}</h2>
        <p>size: {dimensions}</p>
        <OptionQuantity type={'cart'} getter={getQuantity} index={index} initValue={element.quantity}/>
        <p>price: {element.price}</p>
        <button onClick={onRemove}>remove</button>

      </div>
    );
  });

  return (
    <>
      <p>
        {cart.length > 0
          ? `${cart.reduce(
              (prevVal, nextVal) => prevVal + nextVal.quantity,
              0
            )} items in your shopping bag`
          : "there is no items in your shopping bag"}
      </p>
      <div>{cartGallery}</div>
    </>
  );
};

export default CartContainer;
