const inCart = (cart, product) => {
  const indexOfProduct = cart
    .map((element) => element.id === product.id)
    .indexOf(cart.map((element) => element.variant.id === product.variant.id));
  if (indexOfProduct) {
    return indexOfProduct;
  } else return false;
};

export default inCart;
