import ButtonOption from "../ui/buttons/ButtonOption";
import { useState, useEffect } from "react";
import nextValue from "../../helpers/nextValue";
import sortProducts from "../../helpers/sortProducts";

const OptionSort = (props) => {
  const optionValues = [
    "price",
    "popularity",
    "author",
    "name",
    "date released",
  ];
  const optionOrder = ["up", "down"];

  const [optionSettings, setOptionSettings] = useState({
    value: optionValues[1],
    order: optionOrder[0],
  });

  const valueHandler = () => {
    const nextValue = nextValue(optionValues, optionSettings.value);
    console.log(`new value of option ${nextValue}`);
    setOptionSettings({ ...optionSettings, value: nextValue });
  };

  const orderHandler = () => {
    const nextOrder = nextValue(optionOrder, optionSettings.order);
    console.log(`new value of option ${nextOrder}`);
    setOptionSettings({ ...optionSettings, order: nextOrder });
  };

  useEffect(
    sortProducts(optionSettings.value, optionSettings.order, props.data),
    [optionSettings.value, optionSettings.order]
  );

  const option = (
    <>
      <ButtonOption onClickHandler={valueHandler}>
        {optionSettings.value}
      </ButtonOption>
      <ButtonOption onClickHandler={orderHandler}>
        {optionSettings.order}
      </ButtonOption>
    </>
  );

  return option;
};

export default OptionSort;
