import { createSlice } from "@reduxjs/toolkit";
import findProduct from "../helpers/findProduct";

const cartSlice = createSlice({
  name: "cart",
  initialState: { cart: [] },
  reducers: {
    changeQuantity(state, action) {
      const indexOfProduct = state.cart.indexOf(action.payload)
      state.cart[indexOfProduct].quantity += action.payload.quantity
    },
    addToCart(state, action) {
      const cartProduct = findProduct(state.cart, "id", action.payload.id);

      if (
        cartProduct &&
        action.payload.id === cartProduct.id &&
        action.payload.variant.id === cartProduct.variant.id
      ) {
        console.log(action.payload)
      } else state.cart.push(action.payload);
    },
    removeFromCart(state, action) {
