import ButtonOption from "../ui/buttons/ButtonOption";
import { useState } from "react";
import useNextValue from "../../hooks/useNextValue";
import useSortProdducts from "../../hooks/useSortProducts";

const OptionElement = (props) => {
  const [optionSettings, setOptionSettings] = useState({
    value: optionValues[1],
    order: optionOrder[0],
  });
  const option = <></>;
  switch (props.type) {
    case "sort":
      const optionValues = [
        "price",
        "popularity",
        "author",
        "name",
        "date released",
      ];
      const optionOrder = ["up", "down"];

      const optionValueHandler = () => {
        useSortProdducts(useNextValue(optionValues, optionValues[1]));
      };

      option = (
        <>
          <ButtonOption type="flip" onClick={optionValueHandler}>
            {optionSettings.value}
          </ButtonOption>
          <ButtonOption type="flip" onClick={optionOrderHandler}>
            {optionSettings.order}
          </ButtonOption>
        </>
      );
      break;
  }
  return option;
};

export default OptionElement;
