const sortProducts = (type, order, array) => {
  let arraySorted = array;
  const foundKey = Object.keys(arraySorted[0].type)[0];
  const price = arraySorted[0].type[foundKey][0].price;
  console.log(foundKey, price);
  const expr = "elo";

  switch (type) {
    case "price":
      console.log(`sort by price. Lowest price: ${expr}`);
      arraySorted = arraySorted.sort((a, b) => {
        const foundKeyA = Object.keys(a.type)[0];
        const foundKeyB = Object.keys(a.type)[0];
        a.type[foundKeyA][0].price - b.type[foundKeyB][0].price;
      });
      console.log(arraySorted);
      break;
    case "popularity":
      console.log("sort by popularity");
      console.log(`sort by price. Lowest price: ${expr}`);
      break;
    case "author":
      console.log("sort by author");
      console.log(`sort by price. Lowest price: ${expr}`);
      break;
    case "name":
      console.log("sort by name");
      console.log(`sort by price. Lowest price: ${expr}`);
      break;
    case "date released":
      console.log("sort by date released");
      console.log(`sort by price. Lowest price: ${expr}`);
      break;
    default:
      break;
  }

  if (order === "up") return arraySorted.reverse();
  console.log(arraySorted);
  //   return arraySorted;
};

export default sortProducts;
