    <>
      <div>gallery</div>
      <h1>name</h1>
      <ButtonImage>like</ButtonImage>
      <p>by author</p>
      <p>price</p>
      <OptionSize />
      <OptionQuantity />
      <ButtonMain>add to cart</ButtonMain>
      <p>description</p>
    </>
  );
};

export default ProductContainer;
