import ButtonOption from "../ui/buttons/ButtonOption";

const OptionSize = (props) => {
  const sizes = Object.keys(props.data.variant);
  const initialState = { quantity: 1, size: sizes[0] };
  const [optionSettings, setOptionSettings] = useState(initialState);

  const sizeHandler = () => {};

  return (
    <ButtonOption onClickHandler={sizeHandler}>
      {optionSettings.size}
    </ButtonOption>
  );
};

export default OptionSize;
