const sortProducts = (type, order, array) => {
  let arraySorted = array;

  switch (type) {
    case "price":
      console.log(`sort by price. Lowest price: ${arraySorted.type[0][0].price}`);
      //arraySorted = arraySorted.sort((a, b) => b.type[0][0]?.price - a.type[0][0]?.price)
      console.log(arraySorted)
      break;
    case "popularity":
      console.log("sort by popularity");
      break;
    case "author":
      console.log("sort by author");
      break;
    case "name":
      console.log("sort by name");
      break;
    case "date released":
      console.log("sort by date released");
      break;
    default:
      return arraySorted;
  }

  if (order === "up") return array.reverse();

  return array;
};

export default sortProducts;
