      state.cart.push(action.payload);
      console.log('addToCart')
    },
    inCart (state, action) {
      console.log('inCart start')
      console.log("id: " + action.payload.id)
      console.log("variant.id: " + action.payload.variant.id)
      state.cart.findIndex((el, index) => {
        if (el.id === action.payload.id && el.variant.id === action.payload.variant.id) {
          return index
        }
