      // }
      console.log('inCart end')
    },
    removeFromCart(state, action) {
      //   const index = findProduct(state, action.payload.id);
      //   check variant
      //   state.splice(index);
    },
    changeSize() {},
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice;
