import { useDispatch } from "react-redux";
import { cartActions } from "../../../store/cart-slice";
import OptionQuantity from "../../elements/OptionQuantity";

const GalleryCart = (props) => {
    const dispatch = useDispatch();

    const gallery = props.data.map((element, index) => {
      const dimensions = `${element.variant.dimensions[0]}" x ${element.variant.dimensions[1]}"`;
  
      const getQuantity = (val) => val;
      const onRemove = () => {
        dispatch(cartActions.removeFromCart(index));
      };
  
      return (
        <div key={`${element.id}.${element.variant.id}`}>
          <h2>{element.name}</h2>
          <p>size: {dimensions}</p>
          <OptionQuantity
            type={"cart"}
            changeQuantity={getQuantity}
            index={index}
            initValue={element.quantity}
          />
          <p>price: {element.price}</p>
          <button onClick={onRemove}>remove</button>
        </div>
      );
    });

    return <div>{gallery}</div>
};

export default GalleryCart;
