import { createSlice } from "@reduxjs/toolkit";
import { findProduct } from '../helpers/findProduct';

const cartSlice = createSlice({
    name: "cart",
    initialState: [],
    reducers: {
        addToCart(state, action) {
            const cartProduct = findProduct(state, action.payload);
            state.push(action.payload)
            if(action.payload.id === cartProduct.id && action.payload.variant.size === cartProduct.variant.size) {
                this.changeQuantity()
            }
        },
        removeFromCart(state, action) {
            const index = findProduct(state, action.payload.id)
            //check variant
            state.splice(index)
        },
        changeSize() {
            
        },
        changeQuantity() {

        }
    }
})

export const cartSliceActions = cartSlice.actions

export default cartSlice;
