import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: { cart: [], indexOfElement: null},
  reducers: {
    changeQuantity(state, action) {
      // state.cart[action.payload.indexOfElement].quantity += action.payload.quantity;
      console.log('reducer')
    },
    addToCart(state, action) {
      state.cart.push(action.payload);
      console.log('reducer')
    },
    inCart (state, action) {
      if(state.cart === [])  return state.indexOfElement = null
      for (const el of state.cart) {
        if (`${el.id}.${el.variant.id}` === `${action.payload.id}.${action.payload.variant.id}`) {
          state.indexOfElement = (state.cart.indexOf(el));
        } else state.indexOfElement = null
      }
    },
    removeFromCart(state, action) {
      //   const index = findProduct(state, action.payload.id);
      //   check variant
      //   state.splice(index);
    },
    changeSize() {},
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice;
