import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: [],
  reducers: {
    changeQuantity(state, action) {},
    addToCart(state, action) {
      const idString = action.payload.id + "." + action.payload.variant.id;
      const idArray = state.cart.map(
        (element) => element.id + "." + element.variant.id
      );
      if (state.cart.length === 0 || !idArray.includes(idString)) {
        state.push(action.payload);
      } else {
        const idIndex = idArray.indexOf(idString);
        state.cart[idIndex].quantity += action.payload.quantity;
      }
      console.log(action.payload);
    },
    removeFromCart(state, action) {},
    changeSize(state, action) {},
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice;
