import { useSelector } from "react-redux";

const CartContainer = () => {
  const cart = useSelector((state) => state.cart);
  console.log(cart)

  return (
    <>
    <p>
      {cart.length > 0
        ? `${cart.reduce((prevVal, nextVal) => prevVal.quantity + nextVal.quantity).quantity} items in your shopping bag`
        : "there is no items in your shopping bag"}
    </p>
    </>

  );
};

export default CartContainer;
