import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: { cart: [] },
  reducers: {
    changeQuantity(state, action) {
      state.cart[action.payload].quantity += action.payload.quantity;
      console.log('reducer')
    },
    addToCart(state, action) {
      state.cart.push(action.payload);
      console.log('reducer')
    },
    inCart (state, action) {
      for (const el of state.cart) {
        console.log('yep')
        console.log(`iterable element id: ${el.id}.${el.variant.id}`);
        console.log(`chosen product id: ${action.payload.id}.${action.payload.variant.id}`);
