const sortProducts = (products, callbackFunction, order) => {
  if (order === "down") {
    products.sort((a, b) =>
      a[callbackFunction(a)] > b[callbackFunction(b)] ? 1 : -1
    );
  } else {
    products.sort((a, b) =>
    a[callbackFunction(a)] < b[callbackFunction(b)] ? 1 : -1
    );
  }
};

export default sortProducts;
