import ButtonOption from "../ui/buttons/ButtonOption";
import { useState } from "react";
import nextValue from "../../helpers/nextValue";
import sortProducts from "../../helpers/sortProducts";

const OptionSort = (props) => {
  const optionValues = [
    "price",
    "popularity",
    "author",
    "name",
    "date released",
  ];
  const optionOrder = ["up", "down"];
  
  const [optionSettings, setOptionSettings] = useState({
    value: optionValues[1],
    order: optionOrder[0],
  });

  const valueHandler = () => {
    sortProducts(nextValue(optionValues, optionValues[1]))
  }


  const option = (
    <>
    <button onClick={valueHandler}/>
      <ButtonOption onOptionClick={valueHandler}>{optionSettings.value}</ButtonOption>
      <ButtonOption>{optionSettings.order}</ButtonOption>
    </>
  );

  return option;
};

export default OptionSort;
