import ButtonOption from "../ui/buttons/ButtonOption"

const OptionQuantity = (props) => {

    const onDecrease = () => {
        // if(props.quantity > 1) {
        //     props.setQuantity(props.quantity--)
        // }
        props.quantity > 1 ? props.setQuantity(props.quantity--) : undefined
    }

    const onIncrease = () => {
        // if(props.quantity > 1) {
        //     props.setQuantity(props.quantity--)
        // }
        prop.setQuantity(props.quantity++)
    }
    
    return <><ButtonOption onClickHandler={onDecrease}>-</ButtonOption>{props.quantity}<ButtonOption onClickHandler={onIncrease}>+</ButtonOption></>
}

export default OptionQuantity
