import { createSlice } from "@reduxjs/toolkit";
import { products } from "./products.json";

const allProductsSlice = createSlice({
  name: "all-products",
  initialState: { products },
  reducers: {
    sortProducts(state, payload) {
      switch (payload.type) {
        case "price":
          state.products.sort((a, b) =>
            a.variant.medium.price > b.variant.medium.price ? 1 : -1
          );
          if (payload.order === "down") state.products.reverse();
          break;
        case "popularity":
          state.products.sort((a, b) => (a.sold > b.sold ? 1 : -1));
          if (payload.order === "down") state.products.reverse();
          break;
        case "author":
          state.products.sort((a, b) => (a.author > b.author ? 1 : -1));
          if (payload.order === "down") state.products.reverse();
          break;
        case "name":
          state.products.sort((a, b) => (a.name > b.name ? 1 : -1));
          if (payload.order === "down") state.products.reverse();
          break;
        case "date released":
          state.products.sort((a, b) => (a.since > b.since ? 1 : -1));
          if (payload.order === "down") state.products.reverse();
          break;
        default:
          break;
      }
    },
    buyProduct(state, payload) {},
    likeProduct(state, payload) {},
  },
});

export const allProductsActions = allProductsSlice.actions;

export default allProductsSlice;
