import ButtonOption from "../ui/buttons/ButtonOption";
import { useState } from "react";
import useNextValue from "../../hooks/useNextValue";
import useSortProducts from "../../hooks/useSortProducts";

const OptionElement = (props) => {
  const optionValues = [
    "price",
    "popularity",
    "author",
    "name",
    "date released",
  ];
  const optionOrder = ["up", "down"];

  const [optionSettings, setOptionSettings] = useState({
    value: optionValues[1],
    order: optionOrder[0],
  });

  const optionValueHandler = () => {
    console.log("clicked");
    const value = useNextValue(optionValues, optionSettings.value)
    setOptionSettings({...optionSettings, value})
    useSortProducts(value);
  };

  const option = (
    <>
      <ButtonOption type="flip" onClick={optionValueHandler}>
        {optionSettings.value}
      </ButtonOption>
      <ButtonOption type="flip">{optionSettings.order}</ButtonOption>
    </>
  );
  
  return option;
};

export default OptionElement;
