import { createSlice } from "@reduxjs/toolkit";
import findProduct from "../helpers/findProduct";

const cartSlice = createSlice({
  name: "cart",
  initialState: { cart: [] },
  reducers: {
    addToCart(state, action) {
        const cartProduct = findProduct(state.cart, 'id', action.payload.id);

        if (
          cartProduct &&
          action.payload.id === cartProduct.id &&
          action.payload.variant.id === cartProduct.variant.id
        ) {
          console.log("quantity increased");
          this.changeQuantity();
        } else state.push(action.payload);
    },
    removeFromCart(state, action) {
      //   const index = findProduct(state, action.payload.id);
      //   check variant
      //   state.splice(index);
    },
    changeSize() {},
    changeQuantity() {},
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice;
